// src/components/layout/SiteHeader.vue
<template>
  <header>
    <div class="container header-content">
      <a href="/" class="logo">
        <font-awesome-icon :icon="['fas', 'leaf']" />
        <span>绿意生活</span>
      </a>

      <form action="#" class="search" @submit.prevent="performSearch">
        <button type="submit" class="search__button">
          <!--svg图标1-->
        </button>
        <input
            type="text"
            class="search__input"
            id="search__input"
            placeholder="搜索商品..."
            v-model="searchQuery"
            @keyup.enter="performSearch"
            aria-label="搜索商品"
        >
        <button class="mic__button" type="button" @click="startVoiceSearch">
          <div class="mic__icon">
            <!--svg图标2-->
          </div>
        </button>
        <button class="picture__button" type="button" @click="openImageSearch">
          <div class="picture__icon">
            <!--svg图标3-->
          </div>
        </button>
      </form>

      <div class="nav-icons">
        <a
            :href="isLoggedIn ? '#' : '/login'"
            class="nav-icon"
            :class="isLoggedIn ? 'logged_in' : 'not_logged_in'"
            @click.prevent="handleLoginClick"
        >
          <font-awesome-icon :icon="['fas', 'user']" />
          <span v-if="isLoggedIn" class="username">{{ username }}</span>
        </a>

        <a href="#" class="nav-icon" @click.prevent="toggleWishlist">
          <font-awesome-icon :icon="['fas', 'heart']" />
        </a>

        <a href="/shoppingcart" class="nav-icon cart-icon" @click.prevent="goToCart">
          <font-awesome-icon :icon="['fas', 'shopping-cart']" />
          <span class="cart-count" v-if="cartCount > 0">{{ cartCount }}</span>
        </a>
      </div>

      <!-- 用户下拉菜单 -->
      <div v-if="isLoggedIn && showUserMenu" class="user-dropdown">
        <div class="user-info">
          <div class="user-name">{{ username }}</div>
          <div class="user-email">{{ userEmail }}</div>
        </div>
        <ul class="user-menu">
          <li><a href="/profile">个人资料</a></li>
          <li><a href="/orders">我的订单</a></li>
          <li><a href="/settings">设置</a></li>
          <li><button @click="logout">退出登录</button></li>
        </ul>
      </div>
    </div>
  </header>
</template>

<script setup>
import { ref, onMounted } from 'vue';
import { useRouter } from 'vue-router';
import useUserStore from '@/composables/useUserStore';

import '../../assets/css/SiteHeader.css'

const router = useRouter();

// 使用外部定义的状态管理
const { isLoggedIn, username, userEmail, cartCount } = useUserStore();
const showUserMenu = ref(false);
const searchQuery = ref('');

// 初始化加载用户状态
onMounted(() => {
  loadUserState();
});

// 加载用户状态
function loadUserState() {
  const savedState = localStorage.getItem('userState');
  if (savedState) {
    const state = JSON.parse(savedState);
    isLoggedIn.value = state.isLoggedIn || false;
    username.value = state.username || '';
    userEmail.value = state.userEmail || '';
    cartCount.value = state.cartCount || 0;
  }
}

// 搜索功能
function performSearch() {
  if (searchQuery.value.trim()) {
    router.push({ path: '/search', query: { q: searchQuery.value } });
  }
}

// 登录/登出处理
function handleLoginClick() {
  if (isLoggedIn.value) {
    showUserMenu.value = !showUserMenu.value;
  } else {
    router.push('/login');
  }
}

// 用户下拉菜单点击外部区域关闭
document.addEventListener('click', (event) => {
  const dropdown = document.querySelector('.user-dropdown');
  const navIcon = document.querySelector('.nav-icon');

  if (showUserMenu.value && dropdown && !dropdown.contains(event.target) && !navIcon.contains(event.target)) {
    showUserMenu.value = false;
  }
});
</script>
