import { ref } from 'vue'
import { getAllProducts } from '@/api/product.js'

export function useProductFetch() {
    const products = ref([])
    const loading = ref(false)
    const error = ref(null)
    const hasMore = ref(true)

    const page = ref(1)
    const limit = 20

    const fetchProducts = async () => {
        if (loading.value || !hasMore.value) return

        let apiResponse

        try {
            loading.value = true
            error.value = null

            // 请求当前页的商品
            apiResponse = await getAllProducts({ page: page.value, limit })
            console.log('API 响应:', apiResponse)

            // 兼容三种响应结构
            let productsData
            if (Array.isArray(apiResponse)) {
                productsData = apiResponse
            } else if (apiResponse && Array.isArray(apiResponse.data)) {
                productsData = apiResponse.data
            } else if (
                apiResponse &&
                apiResponse.data &&
                Array.isArray(apiResponse.data.data)
            ) {
                productsData = apiResponse.data.data
            } else {
                throw new Error(`无效的API响应格式: ${typeof apiResponse}`)
            }

            if (productsData.length < limit) {
                hasMore.value = false // 没有更多商品了
            }

            products.value.push(...productsData)
            page.value++
        } catch (err) {
            error.value = err.message || '获取商品失败'
            console.error('API 错误详情:', err)

            if (apiResponse) {
                console.warn('API 响应内容:', apiResponse)
            }
        } finally {
            loading.value = false
        }
    }

    const reset = () => {
        products.value = []
        page.value = 1
        hasMore.value = true
    }

    return {
        products,
        loading,
        error,
        hasMore,
        fetchProducts,
        reset
    }
}
