import { ref } from 'vue'
import { getAllProducts } from '@/api/product.js'

export function useProductFetch() {
    const products = ref([])
    const loading = ref(false)
    const error = ref(null)
    const hasMore = ref(true)

    const page = ref(1)
    const limit = ref(20)
    const totalItems = ref(0)

    const fetchProducts = async (category = null) => {
        if (loading.value || !hasMore.value) return

        let apiResponse
        const params = {
            page: page.value,
            limit: limit.value
        }

        if (category) {
            params.category = category
        }

        try {
            loading.value = true
            error.value = null

            apiResponse = await getAllProducts(params)
            console.log('API 响应:', apiResponse)

            let productsData
            if (apiResponse && Array.isArray(apiResponse.data)) {
                productsData = apiResponse.data
                totalItems.value = apiResponse.meta?.totalItems || 0
            } else if (
                apiResponse &&
                apiResponse.data &&
                Array.isArray(apiResponse.data.data)
            ) {
                productsData = apiResponse.data.data
                totalItems.value = apiResponse.data.meta?.totalItems || 0
            } else {
                throw new Error(`无效的API响应格式: ${typeof apiResponse}`)
            }

            if (productsData.length < limit.value) {
                hasMore.value = false
            } else {
                const remaining = totalItems.value - products.value.length
                hasMore.value = remaining > 0
            }

            products.value = [...products.value, ...productsData]
            page.value++
        } catch (err) {
            error.value = err.message || '获取商品失败'
            console.error('API 错误详情:', err)
        } finally {
            loading.value = false
        }
    }

    const reset = () => {
        products.value = []
        page.value = 1
        hasMore.value = true
        totalItems.value = 0
        error.value = null
    }

    return {
        products,
        loading,
        error,
        hasMore,
        totalItems,
        fetchProducts,
        reset
    }
}
