import { ref } from 'vue'
import { getAllProducts } from '@/api/product.js'

export function useProductFetch(initialLimit = 999999) {
    const products = ref([])
    const loading = ref(false)
    const error = ref(null)
    const hasMore = ref(true)
    const totalItems = ref(0)

    const page = ref(1)
    const limit = ref(initialLimit)

    const fetchProducts = async (category = null) => {
        if (loading.value || !hasMore.value) return

        let apiResponse
        const params = {
            page: page.value,
            limit: limit.value
        }

        if (category) {
            params.category = category
        }

        try {
            loading.value = true
            error.value = null

            apiResponse = await getAllProducts(params)
            console.log('API 响应:', apiResponse)

            console.log('完整 API 响应结构:', JSON.stringify(apiResponse, null, 2))

            let productsData = []
            let totalCount = 0

            // 处理不同的响应结构
            if (apiResponse && Array.isArray(apiResponse)) {
                productsData = apiResponse
                totalCount = apiResponse.length
            } else if (apiResponse && apiResponse.data && Array.isArray(apiResponse.data)) {
                productsData = apiResponse.data
                totalCount = apiResponse.meta?.totalItems || apiResponse.total || productsData.length
            } else if (apiResponse && apiResponse.data && apiResponse.data.data && Array.isArray(apiResponse.data.data)) {
                productsData = apiResponse.data.data
                totalCount = apiResponse.data.meta?.totalItems || apiResponse.data.total || productsData.length
            } else {
                console.warn('无法识别的 API 结构，尝试直接使用响应:', apiResponse)
                productsData = apiResponse || []
                totalCount = productsData.length
            }

            totalItems.value = totalCount

            if (productsData.length < limit.value) {
                hasMore.value = false
            } else {
                const remaining = totalItems.value - (products.value.length + productsData.length)
                hasMore.value = remaining > 0
            }

            products.value = [...products.value, ...productsData]
            page.value++

            console.log(`加载 ${productsData.length} 个商品，总计 ${products.value.length}/${totalItems.value}`)
            console.log(`还有更多商品: ${hasMore.value}`)

        } catch (err) {
            error.value = err.message || '获取商品失败'
            console.error('API 错误详情:', err)
            if (apiResponse) {
                console.warn('API 响应内容:', apiResponse)
            }
        } finally {
            loading.value = false
        }
    }

    const reset = () => {
        products.value = []
        page.value = 1
        hasMore.value = true
        totalItems.value = 0
        error.value = null
    }

    return {
        products,
        loading,
        error,
        hasMore,
        totalItems,
        fetchProducts,
        reset
    }
}
