import { ref } from 'vue'
import { getAllProducts } from '@/api/product.js'

export function useProductFetch(initialCategory = null) {
    const products = ref([])
    const loading = ref(false)
    const error = ref(null)
    const hasMore = ref(true)
    const currentCategory = ref(initialCategory)

    const page = ref(1)
    const limit = 20

    const setCategory = (category) => {
        reset()
        currentCategory.value = category
    }

    const fetchProducts = async () => {
        if (loading.value || !hasMore.value) return

        let apiResponse
        const queryParams = {
            page: page.value,
            limit
        }

        // 添加分类参数
        if (currentCategory.value) {
            queryParams.category = currentCategory.value
        }

        try {
            loading.value = true
            error.value = null

            apiResponse = await getAllProducts(queryParams)
            console.log('API 响应:', apiResponse)

            let productsData
            if (Array.isArray(apiResponse)) {
                productsData = apiResponse
            } else if (apiResponse && Array.isArray(apiResponse.data)) {
                productsData = apiResponse.data
            } else if (
                apiResponse &&
                apiResponse.data &&
                Array.isArray(apiResponse.data.data)
            ) {
                productsData = apiResponse.data.data
            } else {
                throw new Error(`无效的API响应格式: ${typeof apiResponse}`)
            }

            if (productsData.length < limit) {
                hasMore.value = false
            }

            products.value = [...products.value, ...productsData]
            page.value++
        } catch (err) {
            error.value = err.message || '获取商品失败'
            console.error('API 错误详情:', err)
        } finally {
            loading.value = false
        }
    }

    const reset = () => {
        products.value = []
        page.value = 1
        hasMore.value = true
        error.value = null
    }

    return {
        products,
        loading,
        error,
        hasMore,
        currentCategory,
        setCategory,
        fetchProducts,
        reset
    }
}
