// @/composables/useUserStore.js
import { ref, computed } from 'vue';

// 全局状态
const globalState = ref({
    isLoggedIn: false,
    username: '',
    userEmail: '',
    cartCount: 0
});

// 初始化时从本地存储加载状态
const initState = () => {
    const savedState = localStorage.getItem('userState');
    if (savedState) {
        try {
            const parsedState = JSON.parse(savedState);
            globalState.value = {
                ...globalState.value,
                ...parsedState
            };
        } catch (e) {
            console.error('Failed to parse user state', e);
        }
    }
};

// 初始化加载
initState();

export function useUserStore() {
    // 计算属性提供响应式访问
    const isLoggedIn = computed(() => globalState.value.isLoggedIn);
    const username = computed(() => globalState.value.username);
    const userEmail = computed(() => globalState.value.userEmail);
    const cartCount = computed(() => globalState.value.cartCount);

    // 更新购物车数量
    const updateCartCount = (count) => {
        globalState.value.cartCount = count;
        saveState();
    };

    // 登录方法
    const login = (userData) => {
        globalState.value = {
            isLoggedIn: true,
            username: userData.username || '',
            userEmail: userData.email || '',
            cartCount: userData.cartCount || 0
        };
        saveState();
    };

    // 登出方法
    const logout = () => {
        globalState.value = {
            isLoggedIn: false,
            username: '',
            userEmail: '',
            cartCount: 0
        };
        localStorage.removeItem('userState');
        localStorage.removeItem('token');
    };

    // 保存状态到本地存储
    const saveState = () => {
        localStorage.setItem('userState', JSON.stringify(globalState.value));
    };

    return {
        isLoggedIn,
        username,
        userEmail,
        cartCount,
        login,
        logout,
        updateCartCount
    };
}
