import axios from './axios'; // 你已有的 axios 实例

// 根据用户id获取购物车数据
export async function fetchCartByUserId(userId) {
    try {
        const response = await axios.get(`/cart/user/${userId}`);
        return response;
    } catch (error) {
        console.error('获取购物车失败', error);
        return { status: error.response?.status || 500, data: { status: 'fail', message: error.message } };
    }
}
